import React, { Component } from 'react';
import ProductRow from './ProductRow';

class ProductTable extends Component {
  constructor(props) {
    super(props);
    this.handleDestroy = this.handleDestroy.bind(this);
    this.handleUpdate = this.handleUpdate.bind(this);
  }

  handleUpdate(product) {
    this.props.onEdit(product);
  }

  handleDestroy(id) {
    this.props.onDestroy(id);
  }

  render() {
    let productsArray = Object.keys(this.props.products)
      .map((pid) => this.props.products[pid])
      .filter((p) => p && p.product); // prevent undefined crash

    let rows = [];

    productsArray.forEach((product) => {
      if (!product || !product.product) return;

      if (
        product.product.name
          .toLowerCase()
          .indexOf(this.props.filterText.toLowerCase()) === -1
      ) {
        return;
      }

      rows.push(
        <ProductRow
          product={product}
          key={product.id}
          onDestroy={this.handleDestroy}
          onEdit={this.handleUpdate}
        />
      );
    });

    return (
      <div>
        <table className="table table-striped table-sm">
          <thead className="thead-dark">
            <tr>
              <th>Product ID</th>
              <th>Name</th>
              <th>Category</th>
              <th>Price</th>
              <th>In Stock?</th>
              <th>&nbsp;</th>
              <th>&nbsp;</th>
            </tr>
          </thead>
          <tbody>{rows}</tbody>
        </table>
      </div>
    );
  }
}

export default ProductTable;
